Create database Food_DB
use Food_DB
create table Categories(
	[CategoryID] int identity primary key,
	[CategoryName] nvarchar(100)
)

create table Products(
	[ProductID] int identity primary key,
	[CategoryID] int foreign key references [Categories](CategoryID),
	[ProductName] nvarchar(100),
	[OrginalPrice] float,
	[Description] ntext,
	[Quantity] int,
	[Picture1] varchar(200),
	[Picture2] varchar(200),
	[DateImport] datetime,
)

create table Contact(
	[ContactID] int identity primary key,
	[CustomerName] nvarchar(50),
	[EmailContact] varchar(100),
	[Phone] varchar(20),
	[SocialNetwork] varchar(100),
	[SocialNetwork1] varchar(100)
)

create table Users(
	[UserName] varchar(100) not null primary key,
	[ContactID] int foreign key references Contact(ContactID),
	[PassWord] varchar(100) not null,
	[Email] varchar(100),
	[Role] bit not null
)


create table Orders(
	[OrderID] int identity primary key,
	[UserName] varchar(100) foreign key references Users(UserName),
	[OrderDate] datetime,
	[CustomerName] nvarchar(100),
	[Address] nvarchar(100),
	[Email] varchar(100),
	[Phone] varchar(20),
	[TotalPrice] float
)

create table OrderDetail(
	[OrderDetailID] int identity primary key,
	[OrderID] int foreign key references Orders(OrderID),
	[ProductID] int foreign key references Products(ProductID),
	[Quantity] int,
	[PricePerProduct] float
)

create table WishList(
	[WishListID] int identity primary key,
	[UserName] varchar(100) foreign key references Users(UserName),
	[ProductID] int foreign key references Products(ProductID)
)

